mapper.select:
SELECT A.REVIEW_ID, A.REVIEW_SJ, A.REVIEW_CN, A.SKIN_TYPE, A.REVIEW_PRODUCT, A.INQIRE_CO, A.CREAT_IP, A.NOTICE_AT, A.OTHBC_AT, A.USE_AT, A.ATCH_FILE_ID, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, (SELECT CONCAT(X.STRE_FILE_NM,'.',X.FILE_EXTSN) FROM LETTNFILEDETAIL X WHERE X.ATCH_FILE_ID = A.ATCH_FILE_ID AND UPPER(FILE_EXTSN) IN('GIF','JPG','JPEG','BMP','PNG') ORDER BY FILE_SN LIMIT 1 )AS ATCH_FILE_NM FROM REVIEW A
ORDER BY A.FRST_REGIST_PNTTM DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
update REVIEW set REVIEW_SJ=#{reviewSj}, REVIEW_CN=#{reviewCn}, SKIN_TYPE=#{skinType}, REVIEW_PRODUCT=#{reviewProduct}, NOTICE_AT=#{noticeAt}, OTHBC_AT=#{othbcAt},
ATCH_FILE_ID=#{atchFileId},
LAST_UPDT_PNTTM=NOW(), LAST_UPDUSR_ID=#{userId} where REVIEW_ID=#{reviewId}
and FRST_REGISTER_ID=#{userId}

mapper.delete:

update REVIEW set USE_AT = 'N'
where REVIEW_ID = #{reviewId}

	and FRST_REGISTER_ID = #{userId}
